mapper.select:
SELECT 
	EXISTS (SELECT 1 
			FROM USER 
			WHERE NAME = #{name} 
			AND EMAIL = #{email}
			AND IS_DELETE = FALSE)

SELECT ID
FROM USER
WHERE NAME = #{name} 
AND EMAIL = #{email}
AND IS_DELETE = FALSE

SELECT ID
FROM USER
WHERE ID = #{id} 
AND EMAIL = #{email}
AND IS_DELETE = FALSE

mapper.update:
UPDATE USER
SET PASSWORD = #{encodePw}
WHERE ID = #{id}
AND IS_DELETE = FALSE
