mapper.select:
SELECT EXISTS (
			   SELECT 1 
				 FROM USER						
				WHERE NICKNAME = #{nickname}
)

SELECT EXISTS (
			   SELECT 1 
				 FROM USER 						
				WHERE EMAIL = #{email}
)

SELECT EXISTS (
			   SELECT 1 
				 FROM USER						
				WHERE NAME = #{name} 
				  AND EMAIL = #{email}
)

SELECT ID
  FROM USER										
 WHERE NAME = #{name} 
   AND EMAIL = #{email}

SELECT USER_ID
  FROM USER										
 WHERE ID = #{id} 
   AND EMAIL = #{email}

mapper.update:
UPDATE USER										
   SET PASSWORD = #{password}
 WHERE USER_ID = #{userId}
