mapper.select:
SELECT C.COMMENT_ID AS commentId , C.WRITER_ID AS writerId , C.CONTENT AS content , C.CREATE_AT AS createAt
, U.NICKNAME AS nickname , U.PROFILE_IMAGE AS profileImage
FROM COMMENT C JOIN USER U ON C.WRITER_ID = U.USER_ID WHERE RECIPE_ID = #{recipeId} AND PARENT_COMMENT_ID IS NULL AND STATUS = #{statusPublic} ORDER BY C.CREATE_AT DESC LIMIT #{size} OFFSET #{offset}

SELECT COUNT(*)
  FROM COMMENT
 WHERE RECIPE_ID = #{recipeId}
   AND STATUS = #{statusPublic}

SELECT COUNT(*)
  FROM COMMENT
 WHERE PARENT_COMMENT_ID = #{parentCommentId}
   AND STATUS = #{statusPublic}

SELECT RECIPE_ID			AS recipeId
	 , PARENT_COMMENT_ID 	AS parentCommentId
  FROM COMMENT
 WHERE COMMENT_ID = #{parentCommentId}
   AND STATUS = #{statusPublic}

mapper.insert:
INSERT INTO COMMENT (
						 CONTENT 
					   , RECIPE_ID 
					   , WRITER_ID 
					   , PARENT_COMMENT_ID 
)
VALUES (
		#{insertDTO.content} 
	  , #{insertDTO.recipeId} 
	  , #{userId} 
	  , #{insertDTO.parentCommentId} 
)

mapper.update:
UPDATE COMMENT
   SET STATUS = #{commentStatus}
 WHERE COMMENT_ID = #{commentId}

mapper.select:
SELECT WRITER_ID AS writerId
  FROM COMMENT
 WHERE COMMENT_ID = #{commentId}
   AND STATUS = #{statusPublic}

mapper.update:
UPDATE COMMENT
   SET CONTENT = #{updateDTO.content}
 WHERE COMMENT_ID = #{updateDTO.commentId}
