mapper.insert:
INSERT INTO RECIPE (
					TITLE
				  , DESCRIPTION
				  , THUMBNAIL
				  , VIDEO_ID
				  , SERVING
				  , DURATION
				  , LEVEL
				  , TIP
				  , STATUS
				  , CATEGORY_CUISINE_ID
				  , CATEGORY_PURPOSE_ID
				  , WRITER_ID
)
VALUES (
		#{title}
	  , #{description}
	  , #{thumbnail}
	  , #{videoId}
	  , #{serving}
	  , #{duration}
	  , #{level}
	  , #{tip}
	  , #{status}
	  , #{categoryCuisineId}
	  , #{categoryPurposeId}
	  , #{writerId}
)

mapper.select:
SELECT R.TITLE , R.DESCRIPTION , R.THUMBNAIL , R.VIDEO_ID AS videoId , R.SERVING , R.DURATION , R.LEVEL , R.TIP , R.STATUS , R.CATEGORY_CUISINE_ID , R.CATEGORY_PURPOSE_ID
, I.NAME , I.QUANTITY , I.REMARK
, S.RECIPE_SEQ_ID , S.DESCRIPTION , S.IMAGE FROM RECIPE AS R LEFT JOIN RECIPE_INGREDIENT AS I ON R.RECIPE_ID = I.RECIPE_ID LEFT JOIN RECIPE_SEQ AS S ON R.RECIPE_ID = S.RECIPE_ID WHERE R.RECIPE_ID = #{recipeId} AND R.WRITER_ID = #{userId}

mapper.update:
		UPDATE RECIPE
		   SET TITLE = #{title}
		   	 , DESCRIPTION = #{description}
        	 , THUMBNAIL = #{thumbnail}
        	 , VIDEO_ID = #{videoId}
        	 , SERVING = #{serving}
        	 , DURATION = #{duration}
        	 , LEVEL = #{level}
        	 , TIP = #{tip}
        	 , STATUS = #{status}
        	 , CATEGORY_CUISINE_ID = #{categoryCuisineId}
        	 , CATEGORY_PURPOSE_ID = #{categoryPurposeId}
    	 WHERE RECIPE_ID = #{recipeId}
    	   AND WRITER_ID = #{writerId}
