mapper.insert:
INSERT INTO RECIPE (
					TITLE
				  , DESCRIPTION
				  , THUMBNAIL
				  , VIDEO_ID
				  , SERVING
				  , DURATION
				  , LEVEL
				  , TIP
				  , STATUS
				  , CATEGORY_CUISINE_ID
				  , CATEGORY_PURPOSE_ID
				  , CREATE_AT
				  , WRITER_ID
)
VALUES (
		#{title}
	  , #{description}
	  , #{thumbnail}
	  , #{videoId}
	  , #{serving}
	  , #{duration}
	  , #{level}
	  , #{tip}
	  , #{status}
	  , #{categoryCuisineId}
	  , #{categoryPurposeId}
	  , #{createAt}
	  , #{writerId}
)

mapper.update:
		UPDATE RECIPE
		   SET TITLE = #{reqRecipe.title}
		   	 , DESCRIPTION = #{reqRecipe.description}
        	 , THUMBNAIL = #{reqRecipe.thumbnail}
        	 , VIDEO_ID = #{reqRecipe.videoId}
        	 , SERVING = #{reqRecipe.serving}
        	 , DURATION = #{reqRecipe.duration}
        	 , LEVEL = #{reqRecipe.level}
        	 , TIP = #{reqRecipe.tip}
        	 , STATUS = #{reqRecipe.status}
        	 , CATEGORY_CUISINE_ID = #{categoryCuisineId}
        	 , CATEGORY_PURPOSE_ID = #{categoryPurposeId}
    	 WHERE RECIPE_ID = #{recipeId}
    	   AND WRITER_ID = #{writerId}
    	   AND STATUS != #{statusPrivateAdmin}

mapper.delete:
DELETE 
  FROM RECIPE
  WHERE RECIPE_ID = #{recipeId}
    AND WRITER_ID = #{userId}

mapper.insert:
INSERT INTO RECIPE_LIKE (
						 USER_ID
					   , RECIPE_ID
)
VALUES (
		#{userId}
	  , #{recipeId}
)

mapper.delete:
DELETE 
  FROM RECIPE_LIKE
 WHERE USER_ID = #{userId}
   AND RECIPE_ID = #{recipeId}

mapper.insert:
INSERT INTO RECIPE_BOOKMARK (
					  		 USER_ID
						   , RECIPE_ID
)
VALUES (
		#{userId}
	  , #{recipeId}
)

mapper.delete:
DELETE 
  FROM RECIPE_BOOKMARK
 WHERE USER_ID = #{userId}
   AND RECIPE_ID = #{recipeId}

mapper.select:
	SELECT WRITER_ID AS writerId
	  FROM RECIPE 								
 	 WHERE RECIPE_ID = #{recipeId}
	   AND STATUS = #{statusPublic}

SELECT EXISTS (
	   SELECT 1 
		 FROM RECIPE 						
		WHERE WRITER_ID = #{userId}
		  AND RECIPE_ID = #{recipeId}
		  AND STATUS IN (
		  		  		 #{statusPublic}
		  	   	       , #{statusPrivate}
		  				)
)

	SELECT RECIPE_ID
		 , TITLE
		 , DESCRIPTION
		 , THUMBNAIL
		 , CREATE_AT
	  FROM RECIPE
	 WHERE WRITER_ID = #{accountId}
	   AND STATUS = #{statusPublic}
  ORDER BY CREATE_AT DESC
	 LIMIT #{size}
	OFFSET #{offset}

	SELECT RECIPE_ID
		 , TITLE
		 , DESCRIPTION
		 , THUMBNAIL
		 , CREATE_AT
		 , CASE WHEN STATUS = #{statusPrivateAdmin} 
		   		THEN 1 
		   		ELSE 0 
		   		 END AS IS_PRIVATE_ADMIN
	  FROM RECIPE
	 WHERE WRITER_ID = #{userId}
	   AND STATUS IN (
	   				  #{statusPrivate}
	   				, #{statusPrivateAdmin}
	   				 ) 
  ORDER BY CREATE_AT DESC
	 LIMIT #{size}
	OFFSET #{offset}

SELECT COUNT(RECIPE_ID)
  FROM RECIPE
 WHERE WRITER_ID = #{accountId}
   AND STATUS = #{statusPublic}

		SELECT COUNT(RECIPE_ID)
		  FROM RECIPE
		 WHERE WRITER_ID = #{userId}
   		   AND STATUS IN (
   				  		  #{statusPrivate}
   						, #{statusPrivateAdmin}
   				  		 )

SELECT CREATE_AT
  FROM RECIPE
 WHERE RECIPE_ID = #{recipeId}
   AND WRITER_ID = #{userId}

SELECT R.THUMBNAIL AS RECIPE_THUMBNAIL , R.RECIPE_ID AS RECIPE_ID , R.TITLE AS RECIPE_TITLE , R.WRITER_ID AS WRITER_ID , U.NICKNAME AS WRITER_NICKNAME , U.PROFILE_IMAGE AS WRITER_PROFILE_IMAGE , COUNT(DISTINCT C.COMMENT_ID) AS RECIPE_COMMENT_COUNT , COUNT(DISTINCT RL.RECIPE_LIKE_ID) AS RECIPE_LIKE_COUNT FROM RECIPE AS R INNER JOIN USER AS U ON R.WRITER_ID = U.USER_ID LEFT JOIN RECIPE_LIKE AS RL ON R.RECIPE_ID = RL.RECIPE_ID LEFT JOIN COMMENT AS C ON R.RECIPE_ID = C.RECIPE_ID WHERE R.STATUS = #{statusRecipePublic}
AND ( R.TITLE LIKE CONCAT('%', #{keyword}, '%') OR R.DESCRIPTION LIKE CONCAT('%', #{keyword}, '%') OR EXISTS ( SELECT 1 FROM RECIPE_INGREDIENT AS RI WHERE RI.RECIPE_ID = R.RECIPE_ID AND RI.NAME LIKE CONCAT('%', #{keyword}, '%') ) )
AND R.CATEGORY_CUISINE_ID = #{categoryCuisineId}
AND R.CATEGORY_PURPOSE_ID = #{categoryPurposeId}
GROUP BY R.RECIPE_ID , R.TITLE , R.WRITER_ID , U.NICKNAME , U.PROFILE_IMAGE LIMIT #{size} OFFSET #{offset}

  		SELECT COUNT(R.RECIPE_ID)
 		  FROM RECIPE AS R
  	     WHERE R.STATUS = #{statusRecipePublic} 

		   AND (
		        R.TITLE LIKE CONCAT('%', #{keyword}, '%')
		        OR R.DESCRIPTION LIKE CONCAT('%', #{keyword}, '%')
		        OR EXISTS (
		          		   SELECT 1 
		          			 FROM RECIPE_INGREDIENT AS RI
		          			WHERE RI.RECIPE_ID = R.RECIPE_ID
		          			  AND RI.NAME LIKE CONCAT('%', #{keyword}, '%')
		        		   )
		        )


		   AND R.CATEGORY_CUISINE_ID = #{categoryCuisineId}


		   AND R.CATEGORY_PURPOSE_ID = #{categoryPurposeId}

SELECT COUNT(R.RECIPE_ID)
  FROM RECIPE 									AS R	
 WHERE R.STATUS = #{statusRecipePublic}
   AND R.RECIPE_ID IN (
    				   SELECT RI.RECIPE_ID
    					 FROM RECIPE_INGREDIENT AS RI	
    					WHERE RI.NAME IN 
        								 #{ingredient}

    					GROUP BY RI.RECIPE_ID
    				   HAVING COUNT(DISTINCT RI.NAME) = #{ingredientCount}
  )
