mapper.select:
	SELECT USER_ID
		 , ID
		 , PASSWORD
	  FROM USER
 	 WHERE ID = #{id}
 	   AND STATUS != #{statusUserDelete}

SELECT U.USER_ID	   AS HOST_ID
	 , U.NICKNAME	   AS NICKNAME
	 , U.PROFILE_IMAGE AS PROFILE_IMAGE
	 , SUM(
	 	   CASE 
	 	   WHEN R.STATUS = #{statusRecipePublic} 
	 	   THEN 1 
	 	   ELSE 0 
	 	    END
     ) 				   AS RECIPE_PUBLIC_COUNT
     , SUM(
     	   CASE 
    	   WHEN R.STATUS = #{statusRecipePrivate} 
     	   THEN 1 
     	   ELSE 0 
     		END
     ) 				   AS RECIPE_PRIVATE_COUNT
     , (
     	SELECT COUNT(FOLLOWING_ID) 
     	  FROM FOLLOW 
     	 WHERE FOLLOWING_ID = #{hostId}
     ) 				   AS FOLLOWING_COUNT
     , (
     	SELECT COUNT(FOLLOWER_ID) 
     	  FROM FOLLOW 
     	 WHERE FOLLOWER_ID = #{hostId}
     ) 				   AS FOLLOWER_COUNT

     , EXISTS (
  				   SELECT 1 
  				   FROM FOLLOW 
  				   WHERE FOLLOWER_ID = #{userId} 
  				     AND FOLLOWING_ID = #{hostId}
			  ) 				AS IS_FOLLOW

 FROM USER 			    AS U
 LEFT JOIN RECIPE 	    AS R 
   ON U.USER_ID = R.WRITER_ID
WHERE USER_ID = #{hostId}

SELECT U.NICKNAME	   AS NICKNAME
	 , U.PROFILE_IMAGE AS PROFILE_IMAGE
	 , SUM(
	 	   CASE 
	 	   WHEN R.STATUS = #{statusRecipePublic} 
	 	   THEN 1 
	 	   ELSE 0 
	 	    END
     ) 				   AS RECIPE_PUBLIC_COUNT
     , SUM(
     	   CASE 
    	   WHEN R.STATUS = #{statusRecipePrivate}
     	   THEN 1 
     	   ELSE 0 
     		END
     ) 				   AS RECIPE_PRIVATE_COUNT
     , (
     	SELECT COUNT(FOLLOWING_ID) 
     	  FROM FOLLOW 
     	 WHERE FOLLOWING_ID = #{userId}
     ) 				   AS FOLLOWER_COUNT
     , (
     	SELECT COUNT(FOLLOWER_ID) 
     	  FROM FOLLOW 
     	 WHERE FOLLOWER_ID = #{userId}
     ) 				   AS FOLLOWING_COUNT

 FROM USER 			    AS U
 LEFT JOIN RECIPE 	    AS R 
   ON U.USER_ID = R.WRITER_ID
WHERE USER_ID = #{userId}

SELECT EXISTS (
	   			SELECT 1 
		 		  FROM USER 						
				 WHERE USER_ID = #{userId}		
				)

mapper.insert:
