					   FOLLOWER_ID
					 , FOLLOWING_ID
    )
    VALUES (
   		    #{userId}
   		  , #{followId}
   		    )

mapper.delete:
DELETE
  FROM FOLLOW
 WHERE FOLLOWER_ID = #{userId}
   AND FOLLOWING_ID = #{followId}

