mapper.insert:
INSERT 
  INTO REPORT (
  			   REPORT_TYPE					
  			 , 
			   RECIPE_ID


			   COMMENT_ID

  			 , REPORTER_ID					
  			 , REPORTED_ID
  			 , CATEGORY_REPORT_REASON_ID	
  			  )
VALUES (
	    #{reportInsertReq.reportType}
	  , 
	    #{reportInsertReq.recipeId}


	    #{reportInsertReq.commentId}

	  , #{userId}
	  , #{reportInsertReq.reportedId}
	  , #{reportInsertReq.categoryReportReasonId}
		)

mapper.select:
SELECT EXISTS (
			   SELECT 1 
				 FROM REPORT 				
				WHERE REPORTER_ID = #{userId}
				  AND 

				  	  REPORT_TYPE = #{reportDuplicationCheckReq.reportType}
				  AND RECIPE_ID = #{reportDuplicationCheckReq.recipeId}


				  	  REPORT_TYPE = #{reportDuplicationCheckReq.reportType}
				  AND COMMENT_ID = #{reportDuplicationCheckReq.commentId}

			   )

mapper.delete:
DELETE
  FROM FOLLOW
 WHERE FOLLOWER_ID = #{userId}
    OR FOLLOWING_ID = #{userId}

mapper.update:
UPDATE COMMENT
   SET STATUS = #{statusCommentDelete}
 WHERE WRITER_ID = #{userId}
    OR PARENT_COMMENT_ID = #{userId}

mapper.select:
		SELECT COUNT(REPORT_ID)
		  FROM REPORT
    	 WHERE REPORTER_ID = #{userId}

   	SELECT COUNT(DISTINCT_REPORTS.REPORT_TARGET_ID) AS sanction_count
	  FROM (
	    	SELECT DISTINCT RECIPE_ID AS REPORT_TARGET_ID
	    	  FROM REPORT
    		 WHERE REPORTED_ID = #{userId}
      		   AND STATUS = #{statusReportApproved}
	      	   AND RECIPE_ID IS NOT NULL
	    	 UNION
	    	SELECT DISTINCT COMMENT_ID AS REPORT_TARGET_ID
	    	  FROM REPORT
	    	 WHERE REPORTED_ID = #{userId}
      		   AND STATUS = #{statusReportApproved}
	      	   AND COMMENT_ID IS NOT NULL
			) AS DISTINCT_REPORTS;

SELECT U.USER_ID AS USER_ID , U.NICKNAME AS NICKNAME , U.PROFILE_IMAGE AS PROFILE_IMAGE , IFNULL(FOLLOW_CNT.FOLLOWER_COUNT, 0) AS FOLLOWER_COUNT , IFNULL(RECIPE_CNT.RECIPE_COUNT, 0) AS RECIPE_COUNT , IFNULL(LIKE_CNT.LIKE_COUNT, 0) AS LIKE_COUNT , IFNULL(BOOKMARK_CNT.BOOKMARK_COUNT, 0) AS BOOKMARK_COUNT , IFNULL(FOLLOW_CNT.FOLLOWER_COUNT, 0) * 0.4 + (IFNULL(RECIPE_CNT.RECIPE_COUNT, 0) * 0.2) + (IFNULL(LIKE_CNT.LIKE_COUNT, 0) * 0.2) + (IFNULL(BOOKMARK_CNT.BOOKMARK_COUNT, 0) * 0.2) AS POPULARITY_SCORE , CASE WHEN LOGIN_USER_FOLLOW.FOLLOWING_ID IS NOT NULL THEN TRUE ELSE FALSE END AS IS_FOLLOWING
FALSE AS IS_FOLLOWING
FROM USER U LEFT JOIN ( SELECT FOLLOWER_ID , COUNT(FOLLOWER_ID) AS FOLLOWER_COUNT FROM FOLLOW GROUP BY FOLLOWER_ID ) AS FOLLOW_CNT ON FOLLOW_CNT.FOLLOWER_ID = U.USER_ID LEFT JOIN ( SELECT WRITER_ID , COUNT(WRITER_ID) AS RECIPE_COUNT FROM RECIPE WHERE STATUS = #{statusRecipePublic} GROUP BY WRITER_ID ) AS RECIPE_CNT ON RECIPE_CNT.WRITER_ID = U.USER_ID LEFT JOIN ( SELECT R.WRITER_ID , COUNT(R.WRITER_ID) AS LIKE_COUNT FROM RECIPE_LIKE AS RL INNER JOIN RECIPE AS R ON RL.RECIPE_ID = R.RECIPE_ID GROUP BY R.WRITER_ID ) AS LIKE_CNT ON LIKE_CNT.WRITER_ID = U.USER_ID LEFT JOIN ( SELECT R.WRITER_ID , COUNT(R.WRITER_ID) AS BOOKMARK_COUNT FROM RECIPE_BOOKMARK AS RB INNER JOIN RECIPE AS R ON RB.RECIPE_ID = R.RECIPE_ID GROUP BY R.WRITER_ID ) AS BOOKMARK_CNT ON BOOKMARK_CNT.WRITER_ID = U.USER_ID
LEFT JOIN ( SELECT FOLLOWING_ID FROM FOLLOW WHERE FOLLOWER_ID = #{userId} ) AS LOGIN_USER_FOLLOW ON LOGIN_USER_FOLLOW.FOLLOWING_ID = U.USER_ID
WHERE U.STATUS = #{statusUserActive}
AND U.USER_ID != #{userId}
AND U.NICKNAME LIKE CONCAT('%', #{keyword}, '%')
ORDER BY POPULARITY_SCORE DESC , FOLLOWER_COUNT DESC , RECIPE_COUNT DESC , LIKE_COUNT DESC , BOOKMARK_COUNT DESC
LIMIT #{size} OFFSET #{offset}

		SELECT COUNT(U.USER_ID)
		  FROM USER U
		WHERE U.STATUS = #{statusUserActive}

 		  AND U.USER_ID != #{userId}


		  AND U.NICKNAME LIKE CONCAT('%', #{keyword}, '%')
